package com.example.apus_hrm_demo.entity;import com.example.apus_hrm_demo.util.enum_util.Cycle;import com.example.apus_hrm_demo.util.enum_util.PayrollType;import com.fasterxml.jackson.annotation.JsonFormat;import jakarta.persistence.*;import lombok.Getter;import lombok.Setter;import java.time.LocalDate;@Entity@Table(name = "payroll")@Setter@Getterpublic class PayrollEntity extends BaseEntity {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    private Long employeeId;    private Long departmentId;    private Long positionId;    @Enumerated(EnumType.STRING)    private PayrollType type;    @Enumerated(EnumType.STRING)    private Cycle cycle;    @JsonFormat(shape = JsonFormat.Shape.STRING, pattern = "dd/MM/yyyy")    private LocalDate startDate;    private Double totalAllowanceAmount;    private String note;}